import React from 'react';
import {storiesOf, flexGrid, centered} from 'cs-web-components-storybook';
// DO NOT import components use relative path! Only use Registry or
// from component libs(defined externals)
import {StatusIcon} from 'cs-web-components-base';

storiesOf('StatusIcon', {})
    .addDecorator(centered)
    .addDecorator(flexGrid({size: 'xs'}))
    .addWithInfo('default', () => {
        const colors = ["#d00000", "#fdd873", "#191919", "#727272", "#bfbfbf", "#8ea502"];
        return (
            <ul>
                {
                    colors.map((item, index) => (
                        <li key={index}>
                            <StatusIcon
                                label={item}
                                color={item}/>
                        </li>
                    ))
                }
            </ul>
        );
    });
