import React from 'react';
import {storiesOf, knobs, centered} from 'cs-web-components-storybook';
// DO NOT import components use relative path! Only use Registry or
// from component libs(defined externals)
import {Throbber} from 'cs-web-components-base';
import picture from '../stories/resources/squarePicture.png';

const opt = {
    sm: 'sm',
    md: 'md'
};

storiesOf('Throbber', {})
    .addDecorator(centered)
    .addWithInfo('default', () => {
        return (
            <Throbber
                size={knobs.select('size', opt, 'sm')}
                hidden={knobs.boolean('hidden')}
                throbberImage={knobs.boolean('throbberImage') ? picture : null}/>
        );
    });
