import React from 'react';
import {storiesOf, flexGrid, knobs, centered} from 'cs-web-components-storybook';
// DO NOT import components use relative path! Only use Registry or
// from component libs(defined externals)
import {Thumbnail} from 'cs-web-components-base';
import picture from '../stories/resources/JuliaEnver.png';
import fallback from '../../../resources/cis_person_primary.svg';

storiesOf('Thumbnail', {})
    .addDecorator(flexGrid({size: 'xs'}))
    .addDecorator(centered)
    .addWithInfo('default', () => {
        const options = {
            sm: 'sm',
            md: 'md',
            lg: 'lg',
            None: null
        };
        return (
            <ul>
                <li>
                    <Thumbnail
                        size={knobs.select('size', options, 'sm')}
                        imgSrc={picture} />
                </li>
                <li>
                    <Thumbnail
                        size={knobs.select('size', options, 'sm')}
                        fallbackSrc={fallback} />
                </li>
                <li>
                    <Thumbnail
                        size={knobs.select('size', options, 'sm')} />
                </li>
            </ul>
        );
    });
