import React from 'react';
import {storiesOf, wrapContext, appContainer, centered} from 'cs-web-components-storybook';
import {Tiles, getAppSetup, Button} from 'cs-web-components-base';
import {documentObject} from './util.jsx';

const objectUrl = getAppSetup().getIn([
    'storybook', 'stories', componentNameSpace, 'person'
]);

const store = {documentObject};

storiesOf('Tile', {})
    .addDecorator(centered)
    .addDecorator(wrapContext(objectUrl, store))
    .addDecorator(appContainer)
    .addWithInfo(
        'default',
        '',
        () => {
            const {contextObject} = store;
            return (
                <div style={{width: "300px"}}>
                    <Tiles.Tile
                        contextObject={contextObject}
                        headerAttr='system:description'
                        controls={
                            <Button.IconButton
                                iconName="csweb_remove"
                                buttonStyle="auxiliary">
                            </Button.IconButton>
                        }>
                    </Tiles.Tile>
                </div>
            );
        },
        {propTables: [Tiles.Tile]}
    );
