import React from 'react';
import Immutable from 'immutable';
import {storiesOf, appContainer} from 'cs-web-components-storybook';
import {Tree} from 'cs-web-components-base';

const treeNodes = Immutable.fromJS([
    {
        id: '1',
        content: "First Child",
        children: []
    },
    {
        id: '2',
        content: "Second Child",
        children: []
    },
    {
        id: '3',
        content: "Third Child",
        children: []
    },
    {
        id: '4',
        content: "Fourth Child",
        children: []
    }
]);

const rootNode = Immutable.fromJS(
    {
        id: "0",
        content: "Root Node",
        expanded: false,
        children: treeNodes,
        has_children: true
    }
);

storiesOf('Tree', {})
    .addDecorator(appContainer)
    .add('Table', () => {
        return (
            <Tree
                rootNode={rootNode} />
        );
    });
