import React from 'react';
import Immutable from 'immutable';
import {storiesOf, knobs, centered} from 'cs-web-components-storybook';
// DO NOT import components use relative path! Only use Registry or
// from component libs(defined externals)
import {WizardProgressPanel} from 'cs-web-components-base';

const steps = Immutable.List([
    Immutable.Map({'label': "Label 1"}),
    Immutable.Map({'label': "Label 2"}),
    Immutable.Map({'label': "Label 3"})]
);

const options = {
    range: true,
    min: -1,
    max: 3,
    step: 1,
};

storiesOf('WizardProgressPanel', {})
    .addDecorator(centered)
    .addWithInfo('default', () => {
        return (
            <WizardProgressPanel
                position={knobs.number("position", 0, options)}
                steps={steps}/>
        );
    });
