import React from 'react';
import {storiesOf, centered} from 'cs-web-components-storybook';
import {FormControl} from 'cs-web-components-base';

storiesOf('Form Control', {})
    .addDecorator(centered)
    .add('CheckBox', () => (
        <div>
            <FormControl.CheckBox
                    label="Unchecked Checkbox"
                    name="CheckBox"/>
            <FormControl.CheckBox
                    label="Checked Checkbox"
                    defaultChecked={true}
                    name="CheckBox"/>
            <FormControl.CheckBox
                    label="Disabled Checkbox"
                    name="CheckBox"
                    disabled/>
            <FormControl.CheckBox
                    label="Disabled Checked Checkbox"
                    defaultChecked={true}
                    name="CheckBox"
                    disabled/>
            <FormControl.TriStateCheckBox
                        label="Indetermined Checkbox"
                        name="CheckBox"
                        value=""/>
        </div>
    ))
    .add('Radio', () => (
        <div>
            <FormControl.Radio
                    label="Unchecked Radio"
                    name="radioGroup"/>
            <FormControl.Radio
                    label="Checked Radio"
                    defaultChecked={true}
                    name="radioGroup"/>
            <FormControl.Radio
                    label="Disabled Radio"
                    disabled
                    name="radioGroup2"/>
            <FormControl.Radio
                    label="Disabled Checked Radio"
                    defaultChecked={true}
                    disabled
                    name="radioGroup2"/>
        </div>
    ));
