import React from 'react';
import {storiesOf, centered} from 'cs-web-components-storybook';
import Immutable from 'immutable';
// DO NOT import components use relative path! Only use Registry or
// from component libs(defined externals)
import {FormControl} from 'cs-web-components-base';

const items = Immutable.fromJS([
    {
        onClick: () => {},
        button_type: 0,
        label: "Button Type: 0"},
    {
        onClick: () => {},
        button_type: 0,
        label: "Button Type: 0, disabled",
        disabled: true
    },
    {
        onClick: () => {},
        button_type: 1,
        label: "Button Type: 1"
    },
    {
        onClick: () => {},
        button_type: 1,
        label: "Button Type: 1, disabled",
        disabled: true
    }]);

storiesOf('Form Control', {})
    .addDecorator(centered)
    .addWithInfo(
        'FormActions',
        ``,
        () => {
            return (
                <FormControl.FormActions
                    buttons={items}
                />
            );
        }
    );
