import React from 'react';
import {storiesOf, withExample, centered} from 'cs-web-components-storybook';
import {FormControl} from 'cs-web-components-base';
import NumericEditDemo from './examples/NumericEditDemo';
import NumericEditDemoSrc from '!raw-loader!./examples/NumericEditDemo.jsx';
import NumericTypeAheadDemo from './examples/NumericTypeAheadDemo';
import NumericTypeAheadDemoSrc from '!raw-loader!./examples/NumericTypeAheadDemo.jsx';

storiesOf('Form Control', {})
    .addDecorator(centered)
    .add(
        'NumericEdit',
        withExample(
            `Here the example of simple usage:`,
            NumericEditDemoSrc,
            {
                propTablesExclude: [NumericEditDemo]
            }
        )(() => (
            <NumericEditDemo />
        ))
    );

storiesOf('Form Control', {})
    .addDecorator(centered)
    .add(
        'NumericEdit with TypeAhead',
        withExample(
            `Here the example to use NumericEdit powered with type ahead :`,
            NumericTypeAheadDemoSrc,
            {
                propTables: [FormControl.NumericEdit, FormControl.WithTypeAhead()],
                propTablesExclude: [NumericTypeAheadDemo]
            }
        )(() => (
            <NumericTypeAheadDemo />
        ))
    );
