import React from 'react';
import {storiesOf, knobs, centered} from 'cs-web-components-storybook';
import {FormControl} from 'cs-web-components-base';

class SliderStory extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            value: "1"
        };
    }

    render() {
        return (
            <FormControl.Slider
                {...this.props}
                onValueChange = { (value) => {
                    // Interact with the value
                    this.setState({
                        value: {value}
                    });
                }}
            />
        );
    }
}

storiesOf('Form Control', {})
    .addDecorator(centered)
    .addWithInfo(
        'Slider',
        ``,
        () => {
            return (
                <div style = {{width: '200px'}}>
                    <SliderStory
                        label = {knobs.text("label", "Label")}
                        name = {knobs.text("label", "Label")}
                        min = {knobs.text("min", "1")}
                        max = {knobs.text("max", "10")}
                        value = {knobs.text("value", "1")}
                    />
                </div>
            );
        }
    );
