import React from 'react';
import {storiesOf, knobs, centered} from 'cs-web-components-storybook';
import {PropTypes} from 'cs-web-components-externals';
// DO NOT import components use relative path! Only use Registry or
// from component libs(defined externals)
import Spinner from '../../../../../base/js/src/formcontrols/Spinner.jsx';

class SpinnerStory extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            value: 1
        };
        this.onValueChange = this.onValueChange.bind(this);
    }

    onValueChange(value) {
        this.setState({value: value});
    }

    render() {
        return (
            <Spinner
                {...this.props}
                onValueChange={this.onValueChange}
                value={this.state.value}/>
        );
    }
}

SpinnerStory.propTypes = {
    onValueChange: PropTypes.func
};

storiesOf('Form Control', {})
    .addDecorator(centered)
    .addWithInfo(
        'Spinner',
        ``,
        () => {
            return (
                <SpinnerStory
                    onValueChange={() => {}}
                    step={knobs.number("step", 1)}
                    minValue={knobs.number("minValue", 1)}
                    maxValue={knobs.number("maxValue", 10)}
                    name="levels"
                    label="Spinner"
                    decimalPlaces={0}
                />
            );
        }
    );
