import React from 'react';
import {storiesOf, centered} from 'cs-web-components-storybook';
// DO NOT import components use relative path! Only use Registry or
// from component libs(defined externals)
import {FormControl} from 'cs-web-components-base';

class TextAreaStory extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            message: ""
        };
        this.setMessage = this.setMessage.bind(this);
    }

    setMessage(e) {
        this.setState({
            message: e.target.value
        });
    }

    render() {
        return (
            <FormControl.TextArea
                {...this.props}
                onChange={(e) => {this.setMessage(e)}}
                value={this.state.message}/>
        );
    }
}

storiesOf('Form Control', {})
    .addDecorator(centered)
    .addWithInfo(
        'TextArea',
        ``,
        () => {
            return (
                <TextAreaStory
                    label="Some Field"
                    name="TextArea"
                />
            );
        }
    );
