import React from 'react';
import {FormControl} from 'cs-web-components-base';

export default class NumericEditDemo extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            value1: null,
            value2: null
        };
        this.onValue1Change = this.onValue1Change.bind(this);
        this.onValue2Change = this.onValue2Change.bind(this);
    }

    onValue1Change(value) {
        this.setState({value1: value});
    }

    onValue2Change(value) {
        this.setState({value2: value});
    }

    render() {
        return (
            <div>
                <FormControl.NumericEdit
                    name="NumericEditSimpleDemo"
                    placeholder="Numbers allowed, decimal places would be removed"
                    value={this.state.value1}
                    onValueChange={this.onValue1Change}
                />
                <FormControl.NumericEdit
                    name="NumericEditWithRangeDemo"
                    decimalPlaces={2}
                    minValue={0}
                    maxValue={99.99}
                    placeholder="0.00 - 99.99, more then 2 decimal places would be removed"
                    value={this.state.value2}
                    onValueChange={this.onValue2Change}
                />
            </div>
        );
    }
}
