import React from 'react';
import {storiesOf, appContainer} from 'cs-web-components-storybook';
// DO NOT import components use relative path! Only use Registry or
// from component libs(defined externals)
import {i18ndb} from 'cs-web-components-base';

const Format = i18ndb.makeFormat('base');
const formatStr = i18ndb.makeFormatStr('base');

storiesOf('i18n', {})
    .addDecorator(appContainer)
    .add('Localized messages', () => {
        return (
            <table className="table table-bordered table-condensed">
                <thead>
                    <tr>
                        <td></td>
                        <td>Expected</td>
                        <td>Actual</td>
                    </tr>
                </thead>
                <tbody>
                    <tr>
                        <td>Format</td>
                        <td>OK</td>
                        <td><Format messageId='ok' /></td>
                    </tr>
                    <tr>
                        <td>makeFormatStr</td>
                        <td>OK</td>
                        <td>{formatStr('ok')}</td>
                    </tr>
                    <tr>
                        <td>makeFormatStr</td>
                        <td>[Missing label: base.xxx]</td>
                        <td>{formatStr('xxx')}</td>
                    </tr>
                    <tr>
                        <td>formatString</td>
                        <td>Laden ...</td>
                        <td>{i18ndb.formatString('loading', 'pdf')}</td>
                    </tr>
                </tbody>
            </table>
        );
    });
