import React from 'react';
import {storiesOf, withExample} from 'cs-web-components-storybook';
import {SplitterLayout} from 'cs-web-components-base';
import SplitterLayoutDemo from './examples/SplitterLayoutDemo';
import SplitterLayoutDemoSrc from '!raw-loader!./examples/SplitterLayoutDemo.jsx';
import SplitterLayoutVerticalDemo from './examples/SplitterLayoutVerticalDemo';
import SplitterLayoutVerticalDemoSrc from '!raw-loader!./examples/SplitterLayoutVerticalDemo.jsx';
import SplitterLayoutResizerDemo from './examples/SplitterLayoutResizerDemo';
import SplitterLayoutResizerDemoSrc from '!raw-loader!./examples/SplitterLayoutResizerDemo.jsx';
import SplitterLayoutOnChangeDemo from './examples/SplitterLayoutOnChangeDemo';
import SplitterLayoutOnChangeDemoSrc from '!raw-loader!./examples/SplitterLayoutOnChangeDemo.jsx';

storiesOf('Layouts', {}).add(
    'SplitterLayout',
    withExample(
        `Here the example of simple usage:`,
        SplitterLayoutDemoSrc,
        {
            propTables: [
                SplitterLayout,
                SplitterLayout.PrimaryPane,
                SplitterLayout.SecondaryPane
            ],
            propTablesExclude: [SplitterLayoutDemo]
        }
    )(() => (
        <SplitterLayoutDemo />
    ))
);

storiesOf('Layouts', {}).add(
    'SplitterLayout vertical',
    withExample(
        `Here the example of simple usage:`,
        SplitterLayoutVerticalDemoSrc,
        {
            propTables: [
                SplitterLayout,
                SplitterLayout.PrimaryPane,
                SplitterLayout.SecondaryPane
            ],
            propTablesExclude: [SplitterLayoutVerticalDemo]
        }
    )(() => (
        <SplitterLayoutVerticalDemo />
    ))
);

storiesOf('Layouts', {}).add(
    'SplitterLayout resizer types',
    withExample(
        `Here the example of simple usage:`,
        SplitterLayoutResizerDemoSrc,
        {
            propTables: [
                SplitterLayout,
                SplitterLayout.PrimaryPane,
                SplitterLayout.SecondaryPane
            ],
            propTablesExclude: [SplitterLayoutResizerDemo]
        }
    )(() => (
        <SplitterLayoutResizerDemo />
    ))
);

storiesOf('Layouts', {}).add(
    'SplitterLayout with change callback',
    withExample(
        `Here the example of simple usage:`,
        SplitterLayoutOnChangeDemoSrc,
        {
            propTables: [
                SplitterLayout,
                SplitterLayout.PrimaryPane,
                SplitterLayout.SecondaryPane
            ],
            propTablesExclude: [SplitterLayoutOnChangeDemo]
        }
    )(() => (
        <SplitterLayoutOnChangeDemo saveSettings={() => {}} />
    ))
);
