import React from 'react';
import Immutable from 'immutable';
import {storiesOf, appContainer, knobs} from 'cs-web-components-storybook';
import {Table} from 'cs-web-components-base';

const columns = Immutable.fromJS(
    [
        {
            width: 400,
            label: "col1",
            tooltip: "Tooltip for col1",
            id: "col1"
        },
        {
            label: "col2",
            tooltip: "Tooltip for col2",
            id: "col2"
        }
    ]
);

const orderedColumns = Immutable.OrderedSet(
    columns.map(c => c.get('id'))
);

const initSortColumns = Immutable.fromJS(
    [{id: columns.getIn([0, 'id']), desc: true}]
).toOrderedSet();

const rows = Immutable.fromJS([
    {
        id: "0",
        columns: [
            "Hallo",
            "Welt"
        ]
    },
    {
        id: "1",
        columns: [
            "Hallo",
            "Erde"
        ]
    },
    {
        id: "2",
        columns: [
            "Guten Morgen",
            "Erde"
        ]
    },
    {
        id: "3",
        columns: [
            "Guten Morgen",
            "Welt"
        ]
    }

]);


storiesOf('Table', {})
    .addDecorator(appContainer)
    .add('Mock data with default config', () => {
        // import {Table} from 'cs-web-components-base';
        const MyTable = Table.PredefinedTables.DefaultTable();
        return (
            <MyTable rows={rows}
                     title={knobs.text("title", "Simple Table")}
                     columns={columns}
                     orderedColumns={orderedColumns}
                     initSortColumns={initSortColumns}
                     contentHeight={knobs.number('contentHeight (px)', 120) + "px"}
                     condensed={knobs.boolean('condensed', true) ? true : false}
                     className="my-table"/>
        );
    });
