import React from 'react';
import {PropTypes, classNames} from 'cs-web-components-externals';
import {prefixNS} from '../helpers';
import Toolbar from './Toolbar';
import HeaderPanels from './HeaderPanels';
import FooterPanels from './FooterPanels';

export default function(WrappedTable) {
    class DefaultLayout extends React.Component {
        render() {
            const {className, id, ...others} = this.props;
            return (
                <div className={prefixNS('table-layout-default')} id={id}>
                    <div className="clearfix table-layout-header">
                        <HeaderPanels />
                        <Toolbar />
                    </div>
                    <WrappedTable
                        className={classNames(className, 'table-layout-content')}
                        {...others}/>
                    <div className="clearfix table-layout-footer">
                        <FooterPanels />
                    </div>
                </div>
            );
        }
    }

    DefaultLayout.propTypes = {
        className: PropTypes.string,
        id: PropTypes.string,
    };

    return DefaultLayout;
}
