import React from 'react';
import {prefixNS} from '../helpers';
import {ImmutablePropTypes} from 'cs-web-components-externals';
import connect from './connect';

export class FooterPanels extends React.Component {
    render() {
        const {footerPanels} = this.props;
        return (
            <div className={prefixNS('table-header-panels')}>
                {
                    footerPanels ? footerPanels.map((Panel, idx) => <Panel key={String(idx)} />)
                                 : null
                }
            </div>
        );
    }
}

FooterPanels.propTypes = {
    footerPanels: ImmutablePropTypes.list
};

const mapStateToProps = (state) => ({
    footerPanels: state.footerPanels
});

export default connect(mapStateToProps)(FooterPanels);
