import React from 'react';
import {prefixNS} from '../helpers';
import {ImmutablePropTypes} from 'cs-web-components-externals';
import connect from './connect';

export class HeaderPanels extends React.Component {
    render() {
        const {headerPanels} = this.props;
        return (
            <div className={prefixNS('table-header-panels')}>
                {
                    headerPanels ? headerPanels.map((Panel, idx) => <Panel key={String(idx)} />)
                                 : null
                }
            </div>
        );
    }
}

HeaderPanels.propTypes = {
    headerPanels: ImmutablePropTypes.list
};

const mapStateToProps = (state) => ({
    headerPanels: state.headerPanels
});

export default connect(mapStateToProps)(HeaderPanels);
