import ContentBlockLayout from './ContentBlockLayout.jsx';
import Table from './Table';
import Manager from './Manager';
import Scrolled from './Scrolled';
import sortable from './sortable';
import groupable from './groupable';
import filterable from './filterable';
import column_aggregator from './column_aggregator';
import selectable from './selectable';
import columnDragger from './column_dragger';
import columnOrderer from './column_orderer';
import columnResizer from './column_resizer';
import settingPanels from './setting_panels';
import columnHider from './column_hider';
import treeview from './treeview';
import tableToTree from './table_to_tree';
import columnFixer from './column_fixer';

export function getDefaultTableOptions() {
    return {
        Table: Scrolled(Table),
        Layout: ContentBlockLayout,
        providers: [
            sortable, filterable, selectable, groupable, column_aggregator,
            settingPanels
        ],
        columnActions: [
            sortable, filterable, selectable, columnDragger, columnOrderer,
            columnResizer
        ],
        toolbarButtons: [
            sortable, filterable, selectable, settingPanels
        ],
        headerPanels: [groupable],
        footerPanels: [sortable, filterable, selectable],
        settingPanels: [columnHider, groupable, sortable, columnOrderer, columnFixer],
        shortcuts: [filterable],
    };
}

export function getDefaultTreeTableOptions() {
    return {
        Table: Scrolled(Table),
        Layout: ContentBlockLayout,
        providers: [
            tableToTree, filterable, treeview, sortable, selectable, groupable,
            column_aggregator, settingPanels
        ],
        columnActions: [
            sortable, filterable, selectable, columnDragger, columnOrderer,
            columnResizer
        ],
        toolbarButtons: [
            sortable, filterable, selectable, treeview, settingPanels
        ],
        headerPanels: [groupable],
        footerPanels: [sortable, filterable, selectable],
        settingPanels: [columnHider, groupable, sortable, columnOrderer, columnFixer],
        shortcuts: [filterable],
    };
}

export function DefaultTable(options = {}) {
    return Manager(Object.assign({}, getDefaultTableOptions(), options));
}

export const SimpleTable = Manager();

export default {
    DefaultTable,
    getDefaultTableOptions,
    getDefaultTreeTableOptions
};
