import React from 'react';
import {ImmutablePropTypes} from 'cs-web-components-externals';
import ToolbarMenu from './ToolbarMenu';
import connect from './connect';
import ButtonToolbar from '../components/ButtonToolbar.jsx';

export class Toolbar extends React.Component {
    render() {
        const {toolbarButtons} = this.props;
        return (
            <ButtonToolbar>
                {toolbarButtons ? toolbarButtons.map(
                        (Button, idx) => <Button key={String(idx)}/>) : null}
                <ToolbarMenu />
            </ButtonToolbar>
        );
    }
}

Toolbar.propTypes = {
    toolbarButtons: ImmutablePropTypes.list
};

const mapStateToProps = (state) => ({
    toolbarButtons: state.toolbarButtons
});

export default connect(mapStateToProps)(Toolbar);
