export const SEARCHHEADERCOMPONENT_SET_SETTINGS =
    `${componentNameSpace}-SEARCHHEADERCOMPONENT-SET-SETTINGS`;
export const SEARCHHEADERCOMPONENT_SET_VALUES =
    `${componentNameSpace}-SEARCHHEADERCOMPONENT-SET-VALUES`;
export const SEARCHHEADERCOMPONENT_SHOW_CONTROLS =
    `${componentNameSpace}-SEARCHHEADERCOMPONENT-SHOW-CONTROLS`;
export const SEARCHHEADERCOMPONENT_SET_INITURL =
    `${componentNameSpace}-SEARCHHEADERCOMPONENT-SET-INITURL`;
export const SEARCHHEADERCOMPONENT_SET_INITVALUES =
    `${componentNameSpace}-SEARCHHEADERCOMPONENT-SET-INITVALUES`;
export const SEARCHHEADERCOMPONENT_SET_ONSEARCHHEADERSUBMIT =
    `${componentNameSpace}-SEARCHHEADERCOMPONENT-SET-ONSEARCHHEADERSUBMIT`;

export function setSettings(searchHeaderSettings) {
    return {
        type: SEARCHHEADERCOMPONENT_SET_SETTINGS,
        payload: {searchHeaderSettings}
    };
}

export function setCurrentValues(currValues) {
    return {
        type: SEARCHHEADERCOMPONENT_SET_VALUES,
        payload: {currValues}
    };
}

export function showControls(showControl) {
    return {
        type: SEARCHHEADERCOMPONENT_SHOW_CONTROLS,
        payload: showControl
    };
}

export function setInitURL(initURL) {
    return {
        type: SEARCHHEADERCOMPONENT_SET_INITURL,
        payload: {initURL}
    };
}

export function setInitValues(initValues) {
    return {
        type: SEARCHHEADERCOMPONENT_SET_INITVALUES,
        payload: {initValues}
    };
}

export function setOnSearchHeaderSubmit(onSearchHeaderSubmit) {
    return {
        type: SEARCHHEADERCOMPONENT_SET_ONSEARCHHEADERSUBMIT,
        payload: {onSearchHeaderSubmit}
    };
}

export default {
    setSettings, setCurrentValues, showControls, setInitURL, setInitValues, setOnSearchHeaderSubmit,
    SEARCHHEADERCOMPONENT_SET_SETTINGS, SEARCHHEADERCOMPONENT_SET_VALUES,
    SEARCHHEADERCOMPONENT_SHOW_CONTROLS, SEARCHHEADERCOMPONENT_SET_INITURL,
    SEARCHHEADERCOMPONENT_SET_INITVALUES, SEARCHHEADERCOMPONENT_SET_ONSEARCHHEADERSUBMIT
};
