export const SET_COLUMNS = `${componentNameSpace}-SET_COLUMNS`;
export const SET_ORDERED_COLUMNS = `${componentNameSpace}-SET_ORDERED_COLUMNS`;
export const SET_HIDDEN_COLUMNS = `${componentNameSpace}-SET_HIDDEN_COLUMNS`;
export const SET_SELECTED_ROWS = `${componentNameSpace}-SET_SELECTED_ROWS`;
export const SET_FOCUSED_ROW = `${componentNameSpace}-SET_FOCUSED_ROW`;
export const SET_FOCUSED_COLUMN = `${componentNameSpace}-SET_FOCUSED_COLUMN`;
export const SET_COLUMN_ACTIONS = `${componentNameSpace}-SET_COLUMN_ACTIONS`;
export const SET_HEADER_PANELS = `${componentNameSpace}-SET_HEADER_PANELS`;
export const SET_FOOTER_PANELS = `${componentNameSpace}-SET_FOOTER_PANELS`;
export const SET_TOOLBAR_BUTTONS = `${componentNameSpace}-SET_TOOLBAR_BUTTONS`;
export const SET_SETTING_PANELS = `${componentNameSpace}-SET_SETTING_PANELS`;
export const SET_TOOLBAR_MENU = `${componentNameSpace}-SET_TOOLBAR_MENU`;
export const SET_COLUMN_HEADER_CLICKED = `${componentNameSpace}-SET_COLUMN_HEADER_CLICKED`;
export const SET_COLUMN_WIDTH = `${componentNameSpace}-SET_COLUMN_WIDTH`;
export const SET_TABLE_USER_SETTING = `${componentNameSpace}-SET_TABLE_USER_SETTING`;
export const SET_TABLE_SETTINGS_FETCHED = `${componentNameSpace}-SET_TABLE_SETTINGS_FETCHED`;
export const SET_ROW_CLASS_NAME = `${componentNameSpace}-SET_ROW_CLASS_NAME`;
export const SET_TABLE_USER_SETTING_CONFIGURATIONS =
    `${componentNameSpace}-SET_TABLE_USER_SETTING_CONFIGURATIONS`;
export const SET_TABLE_CHANGE_CONFIGURATION =
    `${componentNameSpace}-SET_TABLE_CHANGE_CONFIGURATION`;
export const SET_TABLE_CURRENT_CONFIGURATION =
    `${componentNameSpace}-SET_TABLE_CURRENT_CONFIGURATION`;
export const SET_TABLE_NEW_CONFIGURATION =
    `${componentNameSpace}-SET_TABLE_NEW_CONFIGURATION`;
export const SET_TABLE_REMOVE_CONFIGURATION =
    `${componentNameSpace}-SET_TABLE_REMOVE_CONFIGURATION`;
export const SET_TABLE_RESET_CONFIGURATION =
    `${componentNameSpace}-SET_TABLE_RESET_CONFIGURATION`;
export const ADD_COLUMN_RENDERER = `${componentNameSpace}-ADD_COLUMN_RENDERER`;
export const ADD_UNHIDABLE_COLUMNS = `${componentNameSpace}-ADD_UNHIDABLE_COLUMNS`;
export const REMOVE_UNHIDABLE_COLUMNS = `${componentNameSpace}-REMOVE_UNHIDABLE_COLUMNS`;
export const SET_FIXED_COLUMNS = `${componentNameSpace}-SET_FIXED_COLUMNS`;
export const NAVIGATE = `${componentNameSpace}-NAVIGATE`;
export const SELECT_ALL_ROWS_EVENT = `${componentNameSpace}-SELECT_ALL_ROWS_EVENT`;
export const SELECT_RANGE_EVENT = `${componentNameSpace}-SELECT_RANGE_EVENT`;
export const SET_SELECTABLE_ROWS_AGGREGATION_STATE =
    `${componentNameSpace}-SET_SELECTABLE_ROWS_AGGREGATION_STATE`;
export const SET_RENDER_DIRECTIVES =
    `${componentNameSpace}-SET_RENDER_DIRECTIVES`;
export const SET_FOCUS_TABLE =
    `${componentNameSpace}-SET_FOCUS_TABLE`;
export const RESET_FOCUS_TABLE =
    `${componentNameSpace}-RESET_FOCUS_TABLE`;

export function setTableUserSettings(key) {
    return {
        type: SET_TABLE_USER_SETTING,
        payload: key
    };
}

export function setTableSettingsFetched(settings) {
    return {
        type: SET_TABLE_SETTINGS_FETCHED,
        payload: settings
    };
}

export function setTableUserSettingConfigurations(configurations) {
    return {
        type: SET_TABLE_USER_SETTING_CONFIGURATIONS,
        payload: configurations
    };
}

export function setTableChangeConfiguration(configuration) {
    return {
        type: SET_TABLE_CHANGE_CONFIGURATION,
        payload: configuration
    };
}

export function setTableCurrentConfiguration(configuration) {
    return {
        type: SET_TABLE_CURRENT_CONFIGURATION,
        payload: configuration
    };
}

export function setTableNewConfiguration(configuration) {
    return {
        type: SET_TABLE_NEW_CONFIGURATION,
        payload: configuration
    };
}

export function setTableRemoveConfiguration(configuration) {
    return {
        type: SET_TABLE_REMOVE_CONFIGURATION,
        payload: configuration
    };
}

export function setTableResetConfiguration() {
    return {
        type: SET_TABLE_RESET_CONFIGURATION,
        payload: undefined
    };
}


export function setColumns(columns) {
    return {
        type: SET_COLUMNS,
        payload: columns
    };
}

export function setOrderedColumns(columns) {
    return {
        type: SET_ORDERED_COLUMNS,
        payload: columns
    };
}

export function setHiddenColumns(columns) {
    return {
        type: SET_HIDDEN_COLUMNS,
        payload: columns
    };
}

export function setSelectedRows(rows, selectionAnchor = null) {
    return {
        type: SET_SELECTED_ROWS,
        payload: {rows, selectionAnchor}
    };
}

export function setFocusedRow(row) {
    return {
        type: SET_FOCUSED_ROW,
        payload: row
    };
}

export function setFocusTable() {
    return {
        type: SET_FOCUS_TABLE
    };
}

export function resetFocusTable() {
    return {
        type: RESET_FOCUS_TABLE
    };
}

export function setFocusedColumn(column) {
    return {
        type: SET_FOCUSED_COLUMN,
        payload: column
    };
}

export function setColumnActions(items) {
    return {
        type: SET_COLUMN_ACTIONS,
        payload: items
    };
}

export function setHeaderPanels(items) {
    return {
        type: SET_HEADER_PANELS,
        payload: items
    };
}

export function setFooterPanels(items) {
    return {
        type: SET_FOOTER_PANELS,
        payload: items
    };
}

export function setToolbarButtons(items) {
    return {
        type: SET_TOOLBAR_BUTTONS,
        payload: items
    };
}

export function setToolbarMenu(items) {
    return {
        type: SET_TOOLBAR_MENU,
        payload: items
    };
}

export function setSettingPanels(items) {
    return {
        type: SET_SETTING_PANELS,
        payload: items
    };
}

export function setColumnHeaderClicked(columnID) {
    return {
        type: SET_COLUMN_HEADER_CLICKED,
        payload: columnID
    };
}

export function setColumnWidth(columnID, width) {
    return {
        type: SET_COLUMN_WIDTH,
        payload: {columnID, width}
    };
}

export function setRowClassName(className, rows) {
    return {
        type: SET_ROW_CLASS_NAME,
        payload: {[className]: rows}
    };
}

export function addColumnRenderer(columnID, renderer) {
    return {
        type: ADD_COLUMN_RENDERER,
        payload: {columnID, renderer}
    };
}

export function addUnhidableColumns(columns) {
    return {
        type: ADD_UNHIDABLE_COLUMNS,
        payload: columns
    };
}

export function removeUnhidableColumns(columns) {
    return {
        type: REMOVE_UNHIDABLE_COLUMNS,
        payload: columns
    };
}

export function setFixedColumns(columns) {
    return {
        type: SET_FIXED_COLUMNS,
        payload: columns
    };
}

export function navigate(event) {
    return {
        type: NAVIGATE,
        payload: event
    };
}

export function selectAllRows(selectAll) {
    return {
        type: SELECT_ALL_ROWS_EVENT,
        payload: selectAll
    };
}

export function setSelectableRowsAggregationState(the_state) {
    return {
        type: SET_SELECTABLE_ROWS_AGGREGATION_STATE,
        payload: the_state
    };
}

export function selectRange(from, to, include) {
    return {
        type: SELECT_RANGE_EVENT,
        payload: {from, to, include}
    };
}

export function setRenderDirectives(renderDirectives) {
    return {
        type: SET_RENDER_DIRECTIVES,
        payload: renderDirectives
    };
}

export default {
    SET_COLUMNS, SET_ORDERED_COLUMNS, SET_HIDDEN_COLUMNS,
    SET_SELECTED_ROWS, SET_FOCUSED_ROW, SET_FOCUSED_COLUMN,
    SET_COLUMN_ACTIONS, SET_HEADER_PANELS,
    SET_FOOTER_PANELS, SET_TOOLBAR_BUTTONS, SET_TOOLBAR_MENU,
    SET_SETTING_PANELS, SET_COLUMN_HEADER_CLICKED, SET_COLUMN_WIDTH,
    SET_TABLE_USER_SETTING, SET_TABLE_SETTINGS_FETCHED,
    SET_ROW_CLASS_NAME, ADD_COLUMN_RENDERER,
    setColumns, setOrderedColumns, setHiddenColumns,
    setSelectedRows, setFocusedRow, setFocusedColumn,
    setColumnActions, setHeaderPanels,
    setFooterPanels, setToolbarButtons, setToolbarMenu,
    setSettingPanels, setColumnHeaderClicked, setColumnWidth,
    setTableUserSettings, setTableSettingsFetched,
    setRowClassName, setTableChangeConfiguration,
    setTableCurrentConfiguration, setTableNewConfiguration,
    addColumnRenderer,
    ADD_UNHIDABLE_COLUMNS, addUnhidableColumns,
    REMOVE_UNHIDABLE_COLUMNS, removeUnhidableColumns,
    SET_FIXED_COLUMNS, setFixedColumns,
    SELECT_ALL_ROWS_EVENT, selectAllRows,
    SET_SELECTABLE_ROWS_AGGREGATION_STATE, setSelectableRowsAggregationState,
    SET_RENDER_DIRECTIVES, setRenderDirectives, SET_FOCUS_TABLE, RESET_FOCUS_TABLE
};
