import {connect as rrConnect} from 'cs-web-components-externals';

export default function connect(...args) {
    const mapStateToProps = args.length > 0 ? args[0] : null;
    const mapDispatchToProps = args.length > 1 ? args[1] : null;
    const mergeProps = args.length > 2 ? args[2] : null;
    const options = Object.assign(
        {},
        args.length > 3 ? args[3] : null,
        {storeKey: 'tableStore'}
    );
    return rrConnect(mapStateToProps, mapDispatchToProps, mergeProps, options);
}

