export const SET_FILTER = `${componentNameSpace}-SET_FILTER`;
export const ADD_FILTER_FUNCTION = `${componentNameSpace}-ADD_FILTER_FUNCTION`;
export const REMOVE_FILTER_FUNCTION = `${componentNameSpace}-REMOVE_FILTER_FUNCTION`;
export const SEND_FOCUS_FILTER = `${componentNameSpace}-SEND_FOCUS_FILTER`;
export const RESET_FOCUS_FILTER = `${componentNameSpace}-RESET_FOCUS_FILTER`;

export function setFilter(filter) {
    return {
        type: SET_FILTER,
        payload: filter
    };
}

export function addFilterFunction(filterer) {
    return {
        type: ADD_FILTER_FUNCTION,
        payload: filterer
    };
}

export function removeFilterFunction(filterer) {
    return {
        type: REMOVE_FILTER_FUNCTION,
        payload: filterer
    };
}

export function sendFocusFilter() {
    return {
        type: SEND_FOCUS_FILTER,
    };
}

export function resetFocusFilter() {
    return {
        type: RESET_FOCUS_FILTER,
    };
}

export default {
    SET_FILTER, setFilter,
    ADD_FILTER_FUNCTION, addFilterFunction,
    REMOVE_FILTER_FUNCTION, removeFilterFunction,
    SEND_FOCUS_FILTER, sendFocusFilter,
    RESET_FOCUS_FILTER, resetFocusFilter,
};
