export const SET_INIT_GROUP_COLUMNS = `${componentNameSpace}-SET_INIT_GROUP_COLUMNS`;
export const SET_GROUP_COLUMNS = `${componentNameSpace}-SET_GROUP_COLUMNS`;
export const TOGGLE_GROUP = `${componentNameSpace}-TOGGLE_GROUP`;
export const EXPAND_ALL = `${componentNameSpace}-EXPAND_ALL`;

export function setInitGroupColumns(columns) {
    return {
        type: SET_INIT_GROUP_COLUMNS,
        payload: columns
    };
}

export function setGroupColumns(columns) {
    return {
        type: SET_GROUP_COLUMNS,
        payload: columns
    };
}

export function toggleGroup(id) {
    return {
        type: TOGGLE_GROUP,
        payload: id
    };
}

export function expandAll() {
    return {
        type: EXPAND_ALL
    };
}

export default {
    SET_INIT_GROUP_COLUMNS, setInitGroupColumns,
    SET_GROUP_COLUMNS, setGroupColumns,
    TOGGLE_GROUP, toggleGroup,
    EXPAND_ALL, expandAll
};
