export const SET_ALLITEMS = `${componentNameSpace}-SET_ALLITEMS`;
export const SET_PAGESIZE = `${componentNameSpace}-SET_PAGESIZE`;
export const SET_CURRENTPAGE = `${componentNameSpace}-SET_CURRENTPAGE`;
export const SET_PAGESIZEOPTIONS = `${componentNameSpace}-SET_PAGESIZEOPTIONS`;

export function setAllItems(num) {
    return {
        type: SET_ALLITEMS,
        payload: num
    };
}

export function setPageSize(num) {
    return {
        type: SET_PAGESIZE,
        payload: num
    };
}

export function setCurrentPage(num) {
    return {
        type: SET_CURRENTPAGE,
        payload: num
    };
}

export function setPageSizeOptions(options) {
    return {
        type: SET_PAGESIZEOPTIONS,
        payload: options
    };
}

export default {
    SET_ALLITEMS, SET_PAGESIZE, SET_CURRENTPAGE, SET_PAGESIZEOPTIONS,
    setAllItems, setPageSize, setCurrentPage, setPageSizeOptions
};
