import actions from './actions';
import Provider from './Provider';
import reducers, {getSingleSelectionMode} from './reducers';
import AllSelector from './AllSelector';

/**
 * Provides selecting functionality in a table.
 *
 * React Properties
 * ----------------
 * This feature defines some more props for the table component:
 *
 * +-----------------------+----------------+------------------+-------------------------------------------+
 * | Property              | Type           | Default          | Use                                       |
 * +=======================+================+==================+===========================================+
 * | singleSelection       | bool           | true             | Single selection mode                     |
 * +-----------------------+----------------+------------------+-------------------------------------------+
 * | withSelector          | bool           | false            | Whether to display an additional column   |
 * |                       |                |                  | with checkbox for selection               |
 * +-----------------------+----------------+------------------+-------------------------------------------+
 * | onSelectRows          | func           | \-               | Function to be called when item is        |
 * |                       |                |                  | selected. The IDs of selected rows will   |
 * |                       |                |                  | be passed to this function.               |
 * +-----------------------+----------------+------------------+-------------------------------------------+
 * | initSelected          | func           | \-               | Initial selections to pass to the table   |
 * +-----------------------+----------------+------------------+-------------------------------------------+
 *
 * @memberof module:Table
 * @module
 */
export default {
    actions, Provider,
    reducers, getSingleSelectionMode,
    columnActions: [AllSelector],
};
