import {SET_SINGLE_SELECTION_MODE, SET_ALL_SELECTOR} from './actions';

export function singleSelectionMode(state = false, action) {
    switch (action.type) {
        case SET_SINGLE_SELECTION_MODE:
            return action.payload === undefined ? state : action.payload;
        default:
            return state;
    }
}

export function getSingleSelectionMode(state) {
    return state.singleSelectionMode;
}

export function allSector(state = 0, action) {
    switch (action.type) {
        case SET_ALL_SELECTOR:
            return action.payload === undefined ? state : action.payload;
        default:
            return state;
    }
}

export function getAllSelector(state) {
    return state.allSector;
}

export default {
    singleSelectionMode, allSector
};
