import Immutable from 'immutable';
import {SET_TABLE_USER_SETTING_CONFIGURATIONS,
        SET_TABLE_CURRENT_CONFIGURATION} from '../common/actions';

export function settingConfigurations(state = Immutable.Map(), action) {
    switch (action.type) {
        case SET_TABLE_USER_SETTING_CONFIGURATIONS:
            return action.payload;
        default:
            return state;
    }
}

export function currentConfiguration(state = '', action) {
    switch (action.type) {
        case SET_TABLE_CURRENT_CONFIGURATION:
            return action.payload ? action.payload : '';
        default:
            return state;
    }
}

export default {
    settingConfigurations, currentConfiguration
};
