import React from 'react';
import {PropTypes} from 'cs-web-components-externals';
import {Button} from 'react-bootstrap';
import {prefixNS} from '../../helpers';
import {formatStr} from '../../i18n';
import Icon from '../../components/Icon';
import {storeSettings} from './actions';
import connect from '../connect';

// TODO: i18n
class SettingsStoreButton extends React.Component {

    render() {
        return (
                <Button className={prefixNS('table-toolbar-store-settings-button')}
                        onClick={this.props.storeSettings}
                        title={formatStr('table_store_settings')}>
                    <Icon name='csweb_table_store_settings' size="sm"/>
                </Button>
        );
    }
}

SettingsStoreButton.propTypes = {
    storeSettings: PropTypes.func
};

export default connect(null, {storeSettings})(SettingsStoreButton);
