export const SET_INIT_SORT_COLUMNS = `${componentNameSpace}-SET_INIT_SORT_COLUMNS`;
export const SET_SORT_COLUMNS = `${componentNameSpace}-SET_SORT_COLUMNS`;
export const ADD_SORT_FUNCTION = `${componentNameSpace}-ADD_SORT_FUNCTION`;
export const REMOVE_SORT_FUNCTION = `${componentNameSpace}-REMOVE_SORT_FUNCTION`;

export function setInitSortColumns(initSortColumns) {
    return {
        type: SET_INIT_SORT_COLUMNS,
        payload: initSortColumns
    };
}

export function setSortColumns(columns) {
    return {
        type: SET_SORT_COLUMNS,
        payload: columns
    };
}

export function addSortFunction(func) {
    return {
        type: ADD_SORT_FUNCTION,
        payload: func
    };
}

export function removeSortFunction(func) {
    return {
        type: REMOVE_SORT_FUNCTION,
        payload: func
    };
}

export default {
    SET_SORT_COLUMNS, setSortColumns,
    SET_INIT_SORT_COLUMNS, setInitSortColumns,
    ADD_SORT_FUNCTION, addSortFunction,
    REMOVE_SORT_FUNCTION, removeSortFunction
};
