export const SET_TREE_NODES = `${componentNameSpace}-SET_TREE_NODES`;
export const TOGGLE_TREE_NODE = `${componentNameSpace}-TOGGLE_TREE_NODE`;
export const EXPAND_TREE_NODES = `${componentNameSpace}-EXPAND_TREE_NODES`;
export const COLLAPSE_TREE_NODES = `${componentNameSpace}-COLLAPSE_TREE_NODES`;
export const TOGGLE_TREE_VIEW = `${componentNameSpace}-TOGGLE_TREE_VIEW`;
export const FILTER_OUT_NODES = `${componentNameSpace}-FILTER_OUT_NODES`;
export const EXPAND_TREE_LEVEL = `${componentNameSpace}-EXPAND_TREE_LEVEL`;

export function setTreeNodes(treeInfo) {
    return {
        type: SET_TREE_NODES,
        payload: treeInfo
    };
}

export function toggleTreeNode(nodeID) {
    return {
        type: TOGGLE_TREE_NODE,
        payload: nodeID
    };
}

export function expandTreeNodes(nodeIDs) {
    return {
        type: EXPAND_TREE_NODES,
        payload: nodeIDs
    };
}

export function collapseTreeNodes(nodeIDs) {
    return {
        type: COLLAPSE_TREE_NODES,
        payload: nodeIDs
    };
}
export function toggleTreeView() {
    return {
        type: TOGGLE_TREE_VIEW
    };
}
export function filterOutNodes(nodeIDs) {
    return {
        type: FILTER_OUT_NODES,
        payload: nodeIDs
    };
}

export function expandTreeLevel(level) {
    return {
        type: EXPAND_TREE_LEVEL,
        payload: level
    };
}

export default {
    SET_TREE_NODES, setTreeNodes,
    TOGGLE_TREE_NODE, toggleTreeNode,
    EXPAND_TREE_NODES, expandTreeNodes,
    COLLAPSE_TREE_NODES, collapseTreeNodes,
    TOGGLE_TREE_VIEW, toggleTreeView,
    FILTER_OUT_NODES, filterOutNodes,
    EXPAND_TREE_LEVEL, expandTreeLevel
};
