/**
 * @module
 */

import React from 'react';
import {PropTypes, ImmutablePropTypes} from 'cs-web-components-externals';
import {Tile} from './TileHelpers.jsx';

/**
 * A plugin component to show |elements| objects as a small tile, when no specific
 * implementation for the object's class is configured.
 *
 * React Properties
 * ----------------
 *
 * =================  =============  ==================  ====================
 * Property           Type           Default             Use
 * =================  =============  ==================  ====================
 * contextObject      Immutable.Map  \-                  The object to render
 * =================  =============  ==================  ====================
 */
export default function FallbackTile(props) {
    return (
        <Tile contextObject={props.contextObject}
              labelText={props.label}
              headerAttr='system:description'
              fallbackImg={props.contextObject.get('system:icon_link')}
              controls={props.controls}
        />
    );
}
FallbackTile.propTypes = {
    contextObject: ImmutablePropTypes.map.isRequired,
    label: PropTypes.string,
    controls: PropTypes.node
};
