class Registry {
    constructor() {
        this.renderers = new Map();
    }

    registerTreeRenderer(name, renderer) {
        this.renderers.set(name, renderer);
        return renderer;
    }

    getTreeRenderer(name) {
        return this.renderers.get(name);
    }
}

const registry = new Registry();

export default registry;
