import React from 'react';
import {withInfo} from '@storybook/addon-info';

export default function withExample(info, srcCode, options = {}) {
    const merged = (
        <div>
            {info}
            <pre>
                <code>{srcCode}</code>
            </pre>
        </div>
    );
    const mergedOptions = {source: false, ...options, text: merged};
    return withInfo(mergedOptions);
}
