import React from 'react';
import {prefixNS} from './helpers';
//import './flex-grid.scss';
import {classNames} from 'cs-web-components-externals';

export default function flexGrid(options = {}) {
    const size = options.size ? options.size : 'md';
    const clsname = classNames(
        prefixNS('flex-grid'),
        prefixNS(`flex-grid--${size}`)
    );
    return function storyWrapper(story) {
        return (
            <div className={clsname}>
                {story()}
            </div>
        );
    };
}
