import {storiesOf, configure, setAddon, addDecorator} from '@storybook/react';
import renderStorybookUI from '@storybook/ui';
import {withInfo, default as infoAddon} from '@storybook/addon-info';
import {action} from '@storybook/addon-actions';
import Provider from '@storybook/core/dist/client/manager/provider';
import defaultAddons from '@storybook/react/dist/server/addons';  // eslint-disable-line no-unused-vars
import wrapContext from './detail-wrapper';
import appContainer from './app-container';
import flexGrid from './flex-grid.jsx';
import withExample from './addons/withExample';
import '@storybook/addon-knobs/register';
import {withKnobs, boolean, number, select, text, color, array} from '@storybook/addon-knobs';
import centered from '@storybook/addon-centered';

setAddon(infoAddon);
addDecorator(withKnobs);

export const knobs = {
    boolean,
    number,
    select,
    text,
    color,
    array
};

export {
    storiesOf, action, configure, setAddon, addDecorator,
    renderStorybookUI,
    Provider,
    wrapContext,
    appContainer,
    flexGrid,
    withInfo,
    withExample,
    centered
};
