import React from 'react';
import {Provider} from 'cs-web-components-storybook';
import {getAppSetup} from 'cs-web-components-base';

export default class MyProvider extends Provider {
    renderPreview(selectedKind, selectedStory) {
        const el = super.renderPreview(selectedKind, selectedStory);
        return React.cloneElement(el, {
            url: el.props.url.replace(
                'iframe.html',
                getAppSetup().getIn([componentNameSpace, 'previewLink']))
        });
    }
}
