import React from 'react';
import {ImmutablePropTypes} from 'cs-web-components-externals';
import {InfoBox} from 'cs-web-components-base';
import {formatStr} from '../i18n.js';
import DashboardItem from '../DashboardItem.jsx';

export default function ErrorWidget(props) {
    return (
        <DashboardItem item={props.item} title='Error'>
            <InfoBox title={formatStr('error_title')}
                     msg={formatStr('unknown_widget', props.item.toJS())}
                     alertType="danger"
                     iconName="csweb_dashboard" />
        </DashboardItem>
    );
}
ErrorWidget.propTypes = {
    item: ImmutablePropTypes.map.isRequired
};
