import React from 'react';
import {displayHistogram} from './Histo';
import {predefinedData, getIDPostix} from './Data';

/* global d3 */

function generateFakeData() {
    const data = predefinedData;

    return data.map((d) => {
        return {
            label: d.label,
            value: 1000 * Math.random(),
            color: d.color
        };
    });
}

function drawHistogram(containerID, wrapperID) {
    const selfID = "dataHistogram" + getIDPostix();
    const viewport = d3.select("#" + wrapperID);
    viewport.append("g").attr("id", selfID);

    displayHistogram(selfID, generateFakeData(), containerID);
}


export default class Histogram extends React.Component {
    constructor(props) {
        super(props);
        this.id = "dashboard-graph-intern" + getIDPostix();
        this.wrapperID = "dashboard-histogram" + getIDPostix();
    }

    shouldComponentUpdate() {
        return false;
    }

    componentDidMount() {
        drawHistogram(this.id, this.wrapperID);
    }

    render() {
        const style = {
            minWidth: '350px'
        };

        return (
            <div id={this.id}>
                <svg id={this.wrapperID} width="50%" height="300" style={style} />
            </div>
        );
    }
}
