import React, {Component} from 'react';
import {ReactRouter, browserHistory} from 'cs-web-components-externals';
import {getAppSetup, PageFrame} from 'cs-web-components-base';
import FullTextSearch from './Content';

export default class SearchApp extends Component {
    render() {
        const basePath = getAppSetup().getIn(
            ['appSettings', 'basePath']);
        const fulltext_search_route = getAppSetup().getIn(
            ['links', componentNameSpace, 'fulltext_search_route']);
        const route = `${basePath}/${fulltext_search_route}`;
        return (
            <PageFrame.ApplicationFrame>
                <ReactRouter.Router history={browserHistory}>
                    <ReactRouter.Switch>
                        <ReactRouter.Route path={route}
                                           component={FullTextSearch}/>
                        <ReactRouter.Redirect from={basePath} to={route}/>
                    </ReactRouter.Switch>
                </ReactRouter.Router>
            </PageFrame.ApplicationFrame>
        );
    }
}
