import React from 'react';
import Immutable from 'immutable';
import {prefixNS} from '../helpers.js';
import {formatStr} from '../i18n';
import SearchResultFacet from './SearchResultFacet.jsx';
import {PropTypes} from 'cs-web-components-externals';

export default class SearchResultFacets extends React.Component {
    render() {
        const {facetInfo, isFetching} = this.props;
        return (
            <div>
                <div className={`${prefixNS('filter_title')}`}>{formatStr("es_category_label")}</div>
                <div className={prefixNS('result-facets')}>
                    {facetInfo.map(facet =>
                        <SearchResultFacet key={facet.get('name')}
                            facet={facet}
                            isFetching={isFetching}
                            active={facet.get('active')} />)
                    }
                </div>
            </div>
        );
    }
}

SearchResultFacets.propTypes = {
    facetInfo: PropTypes.instanceOf(Immutable.List),
    isFetching: PropTypes.bool.isRequired
};
