/* eslint-env jest */

import React from 'react';
import {shallow} from 'enzyme';
import Spinner from '../Spinner.jsx';

describe('The Spinner component', () => {

    it('shows a Throbber only while fetching', () => {
        const spinner = shallow(
            <Spinner isFetching={false} />
        );

        let throbber = spinner.find('Throbber');
        expect(throbber).toHaveLength(1);
        expect(throbber.prop('hidden')).toBe(true);

        spinner.setProps({isFetching: true});
        throbber = spinner.find('Throbber');
        expect(throbber).toHaveLength(1);
        expect(throbber.prop('hidden')).toBe(false);
    });

});
