export function prefixNS(name) {
    return `${componentNameSpace}-${name}`;
}

export function prefixTM(name) {
    return `cs-taskmanager-web-${name}`;
}

export const ATTR_ID = '@id';

export const INFO = 0;
export const EDIT = 1;

export function getMode(formData) {
    return formData.get('mode') === 'edit' ? EDIT : INFO;
}
