import React from 'react';
import Immutable from 'immutable';
import {connect, PropTypes} from 'cs-web-components-externals';
import TaskForms from './TaskForms';

const TaskFormsWrapper = ({task_object_id, form}) => (
    <TaskForms task_object_id={task_object_id}
               ID={task_object_id}
               forms={Immutable.List([form])} />
);

TaskFormsWrapper.propTypes = {
    task_object_id: PropTypes.string.isRequired,
    form: PropTypes.string
};

function mapStateToProps(state, ownProps) {
    return {
        task_object_id: ownProps.match.params.task_object_id,
        form: ownProps.match.params.form
    };
}

export default connect(mapStateToProps)(TaskFormsWrapper);
