const path = require('path');
const componentNameSpace = "cs-workflow-forms";

var commonConfig = {};
if (process.env.WEBPACK_CONFIG_COMMON === undefined) {
    var top = '../../../../webpack.config.common.js';
    console.warn(`Environment variable WEBPACK_CONFIG_COMMON not set, using ${top}`);
    commonConfig = require(top).makeConfig(componentNameSpace);
} else {
    commonConfig = require(process.env.WEBPACK_CONFIG_COMMON).makeConfig(componentNameSpace);
    console.log(`will merge configs from ${process.env.WEBPACK_CONFIG_COMMON}`);
}

module.exports = Object.assign({}, commonConfig, {
    // here the app specific configs
});
